import React from 'react';

class TodoForm extends React.Component{
    state = {
    text: ""
    };

    handleChange = (event) =>{
        this.setState({
            [event.target.name]: event.target.value
        });
    };



    render(){
        return(
            <div>
                <input 
                    name= 'text'
                    value={this.state.text} 
                    onChange ={this.handleChange}
                    placeholder='Add new todo' ></input>

            </div>
        )
    }
}

export default TodoForm;
